import React, { useRef, useState } from 'react';
import {
  ScrollView,
  StyleSheet,
  View,
  TouchableOpacity,
  Text,
  Dimensions,
  ViewStyle,
} from 'react-native';
import ReviewCard from './ReviewCard';
import CarouselHeader from '../CarouselHeader';

const screenWidth = Dimensions.get('window').width;

const AppReviews = () => {
  const scrollRef = useRef<ScrollView>(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  const reviews = [
    {
      user: {
        name: 'Ajay',
        image:
          'data:image/jpeg;base64,',
      },
      content: 'This app is very good for every student who really wants to study.',
    },
    {
      user: {
        name: 'Neha',
        image:
          'data:image/jpeg;base64,',
      },
      content: 'Helpful and easy to use!',
    },
    {
      user: {
        name: 'Raj',
        image:
          'data:image/jpeg;base64,',
      },
      content: 'Love the UI and experience.',
    },
  ];

  const scrollToIndex = (index: number) => {
    if (scrollRef.current) {
      scrollRef.current.scrollTo({
        x: index * (screenWidth - 40 + 10), // width of ReviewCard + marginRight
        animated: true,
      });
    }
  };

  const handleNext = () => {
    const nextIndex = (currentIndex + 1) % reviews.length;
    setCurrentIndex(nextIndex);
    scrollToIndex(nextIndex);
  };

  const handlePrev = () => {
    const prevIndex = (currentIndex - 1 + reviews.length) % reviews.length;
    setCurrentIndex(prevIndex);
    scrollToIndex(prevIndex);
  };

  // Generate indicator style based on the active index
  const generateIndicatorStyle = (index: number): ViewStyle => ({
    width: 10,
    height: 10,
    borderRadius: 50,
    backgroundColor: currentIndex === index ? '#000' : '#bbb',
    marginHorizontal: 5,
  });

  return (
    <View style={styles.AppReviews}>
      <CarouselHeader head={{headText:'Reviews',btnText:'+ Add'}}/>
      <ScrollView
        ref={scrollRef}
        style={styles.ReviewCarousel}
        horizontal
        pagingEnabled={false}
        showsHorizontalScrollIndicator={false}
        scrollEnabled={false}
      >
        {reviews.map((review, index) => (
          <ReviewCard key={index} review={review} />
        ))}
      </ScrollView>

      {/* Button container and active indicator */}
      <View style={styles.buttonContainer}>
        <TouchableOpacity onPress={handlePrev} style={styles.button}>
          <Text style={styles.buttonText}>{'<'}</Text>
        </TouchableOpacity>

        {/* Indicator Circles */}
        <View style={styles.indicatorContainer}>
          {reviews.map((_, index) => (
            <View key={index} style={generateIndicatorStyle(index)} />
          ))}
        </View>

        <TouchableOpacity onPress={handleNext} style={styles.button}>
          <Text style={styles.buttonText}>{'>'}</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  AppReviews: {
    minHeight: 280,
    paddingVertical: 10,
    paddingHorizontal: 15,
    paddingBottom: 20,
    backgroundColor: '#fff',
    marginBottom:30,
  },
  ReviewCarousel: {
    width: '100%',
    minHeight: 220,
  },
  buttonContainer: {
    flexDirection: 'row',
    paddingHorizontal: 20,
    justifyContent: 'space-between',
    marginTop: 10,
    gap: 20,
    alignItems: 'center', // Align indicators and buttons properly
  },
  button: {
    backgroundColor: '#fff',
    borderRadius: 10,
    paddingHorizontal: 15,
    // iOS Shadow
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 2,
    // Android Shadow
    elevation: 2,
  },
  buttonText: {
    fontSize: 25,
  },
  indicatorContainer: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
  },
});

export default AppReviews;
